		"	#ifdef DOUBLE_SIDED",

		"		vec3 directionalLightWeightingBack = vec3( max( -dotProduct, 0.0 ) );",

		"		#ifdef WRAP_AROUND",

		"			vec3 directionalLightWeightingHalfBack = vec3( max( -0.5 * dotProduct + 0.5, 0.0 ) );",

		"		#endif",

		"	#endif",

		"	#ifdef WRAP_AROUND",

		"		vec3 directionalLightWeightingHalf = vec3( max( 0.5 * dotProduct + 0.5, 0.0 ) );",
